import Check from "../Check/Check"



const ToDoList = ({ notes, setNotes }) => {

    const checked = notes.map(e => (
        <Check key={e.id} data={e} onChange={onChangeChecked}/>
    ));

    const onChangeChecked = e => {
        const { name, checking } = e.target;
        const updateList = notes.map(e => ({
          ...e,
          done: e.id === name ? checking : e.done
        }));
        setNotes(updateList);
      };

      const onClickRemoveItem = e => {
        const updateList = notes.filter(item => !item.done);
        setList(updateList);
      };


    return (
        <div>
            {notes.length ? checked : "No tasks"}
            {notes.length ? (
                <p>
                    <button className="button blue">
                        Delete all done
                    </button>
                </p>
            ) : null}



        </div>
    )
}


export default ToDoList
