import { useState } from "react";

const ToDo = ({ handleAddItem }) => {


    const [userInput, setUserInput] = useState('');

    const handleClick = e => {
        e.preventDefault();

    handleAddItem({
        done: false,
        id: (+new Date()).toString(), userInput
    });
    setUserInput("");

};

    return (
        <form>
            <div>
                <label>Añadir nota:</label>
                <input
                    type='text'
                    value={userInput}
                    onChange={e => setUserInput(e.target.value)} />
                <button
                    disabled={userInput ? "" : "disabled"}
                    type='submit'
                    onClick={handleClick}> Añadir </button>

            </div>

        </form>
    )
}


export default ToDo
