import { useContext, useState } from "react";
import { NoteContext } from "../../context/NoteContext";
import './ToDo.css'
import calendar from '../assets/calendar.png'


const ToDo = ({ id, task, complete, date, setCurrentDate, currentDate, relevance, tag }) => {

    const { setStatusTask } = useContext(NoteContext)
    const [isEditing, setIsEditing] = useState(false);

    const checkTask = e => setStatusTask(id, e.target.checked)

    const handleUpdateDate = () => {
        setCurrentDate(new Date());
        setIsEditing(false);
    }

    const formattedDate = currentDate.toLocaleString('es-ES', {
        year: 'numeric',
        month: 'long',
        day: 'numeric',
        hour: 'numeric',
        minute: 'numeric',
        second: 'numeric'
    });

    let dotColor;

    switch (relevance) {
        case '1':
            dotColor = '#D8A2FF';
            break;
        case '2':
            dotColor = '#A8FFD9';
            break;
        case '3':
            dotColor = '#E7008F';
            break;
        default:
            dotColor = 'black';
    }

    const deleteCoffee = (id) => {
        cart.map((coffee) => {
            if (coffee._id === id) {
                cart.splice(cart.indexOf(coffee), 1)
            }
        })
        setChoose(!choose)
    }

    const handleDelete = (id) => {
        setStatusTask((prevTasks) => prevTasks.filter((task) => task.id !== id));
    }


    return (
        <div className="conteiner-list" style={{ backgroundColor: dotColor }}>
            <button onClick={() => handleDelete(id)}>DELETE</button>
            <div className="card-info" style={{ backgroundColor: 'transparent' }}>
                <div id="check-name">
                    <input type="checkbox" onChange={checkTask} />
                    <h3 className={complete ? 'task-done' : ''}>{task}</h3>
                </div>

                <div id="tag-task">
                    {tag && <p>✨{tag}✨</p>}
                </div>



                <div className="box-date">

                    <div className="calendar-box" id="datetime"> {isEditing ? (
                        <div>
                            <input type="datetime-local"
                                value={currentDate}
                                onChange={(e) => setCurrentDate(e.target.value)}
                                onBlur={() => setIsEditing(false)}
                                autoFocus />
                            <button onClick={handleUpdateDate}>CHANGE</button>
                        </div>) : (

                        <p className="transparent" id="line" onClick={() => setIsEditing(true)}>{formattedDate}</p>
                    )}
                    </div>


                    <div style={{ paddingTop: '25px' }} className="calendar-box">
                        <img src={calendar} alt='calendar' />
                        <p className="transparent">{date}</p>
                    </div>

                </div>
            </div>


        </div>
    )
}


export default ToDo
