import { useContext, useState } from "react";
import { NoteContext } from "../../context/NoteContext";
import './ToDo.css'
import calendar from '../assets/calendar.png'
import flecha from '../assets/flech.png'

const ToDo = ({ id, task, complete, date, currentDate, setCurrentDate, relevance, tag }) => {

    const { setStatusTask } = useContext(NoteContext)
    const checkTask = e => setStatusTask(id, e.target.checked)
    const [isEditing, setIsEditing] = useState(false);

    const handleUpdateDate = () => {
        setCurrentDate(new Date());
        setIsEditing(false);
    }

    const formattedDate = currentDate.toLocaleString('es-ES', {
        year: 'numeric',
        month: 'long',
        day: 'numeric',
        hour: 'numeric',
        minute: 'numeric',
        second: 'numeric'
    });

    let dotColor;

    switch (relevance) {
        case '1':
            dotColor = '#D8A2FF';
            break;
        case '2':
            dotColor = '#A8FFD9';
            break;
        case '3':
            dotColor = '#E7008F';
            break;
        default:
            dotColor = 'black';
    }





    return (
        <div className="conteiner-list" style={{ backgroundColor: dotColor }}>
            <div className="card-info" style={{backgroundColor: 'transparent'}}>
            <svg height="24" width="24" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path d="M0 0h24v24H0z" fill="none"></path><path d="M4.828 21l-.02.02-.021-.02H2.992A.993.993 0 0 1 2 20.007V3.993A1 1 0 0 1 2.992 3h18.016c.548 0 .992.445.992.993v16.014a1 1 0 0 1-.992.993H4.828zM20 15V5H4v14L14 9l6 6zm0 2.828l-6-6L6.828 19H20v-1.172zM8 11a2 2 0 1 1 0-4 2 2 0 0 1 0 4z" fill="currentColor"></path></svg>
                <div id="check-name">
                    <input type="checkbox" onChange={checkTask} />
                    <h3 className={complete ? 'task-done' : ''}>{task}</h3>
                </div>

                <div id="tag-task">
                    {tag && <p>✨{tag}✨</p>}
                </div>



                <div className="box-date">

                    <div className="calendar-box" id="datetime"> {isEditing ? (
                        <input type="datetime-local"
                            value={currentDate.toISOString().slice(0, -8)}
                            onChange={(e) => setCurrentDate(new Date(e.target.value))}
                            onBlur={() => setIsEditing(false)}
                            autoFocus />) : (

                        <p className="transparent" id="line" onClick={() => setIsEditing(true)}>{formattedDate}</p>
                    )}
                    </div>


                    <div style={{ paddingTop: '25px' }} className="calendar-box"><img src={calendar} alt='calendar' /> <p className="transparent">{date}</p> </div>

                </div>
            </div>


        </div>
    )
}


export default ToDo
