import { useContext, useEffect, useState } from "react";
import { NoteContext } from "../../context/NoteContext";
import ToDo from "../ToDo/ToDo";




const ToDoList = () => {

    const { message, deleteTask , setMessege } = useContext(NoteContext)
    const [currentDate] = useState(new Date())
    const [taskList, setTaskList] = useState([])

    useEffect(() => {


        let list = message.map((notes) => {


            return (
                <div key={`${notes.id}`}>

                    <div>
                        <ToDo
                            task={`${notes.tasks}`}
                            date={notes.date}
                            priority={notes.relevance}
                            tag={notes.tag}
                        />
                    </div>
                </div>
            )
        })

        setTaskList(list)
    }, [message])




    return (
        <div style={{ display: 'flex', justifyContent: 'space-evenly', flexWrap: 'wrap' }}>
            {message.map((notes, id) =>
                <ToDo key={id} {...notes} currentDate={currentDate} deleteTask={deleteTask} relevance={notes.relevance} />

            )}
        </div>
    )
}


export default ToDoList
