import { useContext, useState } from "react";
import { NoteContext } from "../../context/NoteContext";
import './Form.css'
import titleImg from '../assets/black-todo.jpeg'


const Form = () => {
    const [task, setTask] = useState('')
    const [date, setDate] = useState('')
    const [tag, setTag] = useState('')
    const { addTask, message, setMessage } = useContext(NoteContext)
    const [currentDate, setCurrentDate] = useState(new Date())
    const [relevance, setRelevance] = useState('')
    const [sortDirection, setSortDirection] = useState('asc')

    const submitNote = (e) => {
        e.preventDefault()
        addTask(task, date, currentDate, relevance, tag)
        setTask('')
        setTag('')
        setDate('')
        setCurrentDate('')
        setRelevance('')
    }

    const handleSort = () => {
        const newSortDirection = sortDirection === 'asc' ? 'desc' : 'asc';
        setSortDirection(newSortDirection);
        setMessage(sortNotes(message, newSortDirection));
    }

    const sortNotes = (notes, direction) => {
        return [...notes].sort((a, b) => {
            const aDate = new Date(a.date).getTime();
            const bDate = new Date(b.date).getTime();
            const compareResult = aDate - bDate;
            return direction === 'asc' ? compareResult : -compareResult;
        });
    }

    const sortedNotes = sortNotes(message, sortDirection);


    return (
        <div id="conteier-form">
            <div id="img-header">
                <img id="img-header" src={titleImg} alt='title-img' />
            </div>
            <form onSubmit={submitNote}>
                <input type="text" className="input-note" value={task} placeholder="Type something here...." onChange={e => setTask(e.target.value)} required />
                <button className="btn-input">Add</button>
                <button className="btn-input" type="button" onClick={handleSort}>
                    Sort by date {sortDirection === 'asc' ? 'ascendente' : 'descendente'}
                </button>
                {task && (
                    <div>
                        <label>Tag:</label>
                        <input type="text" value={tag} onChange={e => setTag(e.target.value)} />
                    </div>
                )}

                {task && (
                    <div>
                        <label>Priority:</label>
                        <select value={relevance} onChange={e => setRelevance(e.target.value)}>
                            <option value="1">Low</option>
                            <option value="2">Medium</option>
                            <option value="3">High</option>
                        </select>
                    </div>
                )}

                {task && (
                    <div>
                        <label>Date to complete:</label>
                        <input type="date" value={date} onChange={e => setDate(e.target.value)} />
                    </div>
                )}




            </form>
            {sortedNotes.map(note => (
                <div key={note.id}>
                    <span>{note.date}</span>
                    <p>{note.task}</p>
                    {note.tag && <p>Etiqueta: {note.tag}</p>}
                </div>
            ))}
        </div>
    )
}



export default Form
