import { useContext, useState } from "react";
import { NoteContext } from "../../context/NoteContext";
import { v4 as uuidv4 } from 'uuid';



const Form = () => {
    const { tags, setNote } = useContext(NoteContext);

    const [input, setInput] = useState('');
    const [selectedTag, setSelectedTag] = useState('sin etiqueta');

    const handleInputChange = (e) => {
        setInput(e.target.value);
    };

    const handleTagChange = (e) => {
        setSelectedTag(e.target.value);
    };

    const handleSubmit = (e) => {
        e.preventDefault();

        if (input.trim() === '') {
            return;
        }
        const newTodo = {
            id: uuidv4(),
            text: input,
            isCompleted: false,
            createdDate: new Date(),
            completedDate: null,
            tag: selectedTag
        };

        setNote((prevTodos) => [...prevTodos, newTodo]);
        setInput('');
    };




    return (
        <div>
            <form onSubmit={handleSubmit}>
                <input type="text" value={input} onChange={handleInputChange} />
                <select value={selectedTag} onChange={handleTagChange}>
                    <option value="sin etiqueta">Sin etiqueta</option>
                    {tags.map((tag) => (
                        <option key={tag.name} value={tag.name} style={{ color: tag.color }}>
                            {tag.name}
                        </option>
                    ))}
                </select>
                <button type="submit">AÃ±adir tarea</button>
            </form>

        </div>
    )
}



export default Form
