import { useContext, useState } from "react";
import { NoteContext } from "../../context/NoteContext";
import { v4 as uuidv4 } from 'uuid';



const Form = () => {
    const { tags, setNote } = useContext(NoteContext);

    const [input, setInput] = useState('');
    const [selectedTag, setSelectedTag] = useState('sin etiqueta');

    const handleInputChange = (e) => {
        setInput(e.target.value);
    };

    const handleTagChange = (e) => {
        setSelectedTag(e.target.value);
    };

    const handleSubmit = (e) => {
        e.preventDefault();

        if (input.trim() === '') {
            return;
        }
        const newTodo = {
            id: uuidv4(),
            text: input,
            isCompleted: false,
            createdDate: new Date(),
            completedDate: null,
            tag: selectedTag
        };

        setNote((prevTodos) => [...prevTodos, newTodo]);
        setInput('');
    };




    return (
        <div>


        </div>
    )
}



export default Form
